package com.example.aademo.util;import com.example.aademo.widget.CircleProgressBarlarge;import android.view.animation.Animation;import android.view.animation.Transformation;import android.widget.TextView;public class CircleProgressAnim extends Animation {	CircleProgressBarlarge mProgressBar;	TextView mTvProgress;	float progress;	public CircleProgressAnim(CircleProgressBarlarge c,TextView tv){		mProgressBar=c;		mTvProgress=tv;		progress=mProgressBar.getProgress();		mProgressBar.setAnimation(this);	}		@Override	protected void applyTransformation(float interpolatedTime, Transformation t) {		super.applyTransformation(interpolatedTime, t);		float p=progress*interpolatedTime;		mTvProgress.setText(Math.floor(p*100) + "%");		mProgressBar.setProgress(p);	}}