package com.example.aademo.util;import android.view.animation.Animation;import android.view.animation.Transformation;import android.widget.TextView;import com.example.aademo.widget.CircleProgressBarlarge;public class CircleProgressAnim extends Animation {	CircleProgressBarlarge mProgressBar;	TextView mTvProgress;	float progress;	public CircleProgressAnim(CircleProgressBarlarge c,TextView tv){		mProgressBar=c;		mTvProgress=tv;		progress=mProgressBar.getProgress();		mProgressBar.setAnimation(this);	}		@Override	protected void applyTransformation(float interpolatedTime, Transformation t) {		super.applyTransformation(interpolatedTime, t);		float p=progress*interpolatedTime;		String pStr=(Math.floor(p*100))+"";		if(pStr.indexOf(".")!=-1){			pStr=pStr.substring(0,pStr.indexOf("."));		}		mTvProgress.setText(pStr+"%");		mProgressBar.setProgress(p);	}		public void setProgress(float progress){		this.progress=progress;	}}