package com.example.aademo.widget;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.widget.ImageView;import com.example.aademo.R;import com.example.aademo.util.PalLog;import com.nineoldandroids.view.ViewHelper;public class ScrollLineView {	ImageView img_smallLine;	private float item_width = 0;	private float line_width = 0;	View mView;	public ScrollLineView(Context context, int width, int pageCount) {		mView = LayoutInflater.from(context).inflate(R.layout.scroll_line, null);		img_smallLine = (ImageView) mView.findViewById(R.id.scroll_img_line);		img_smallLine.post(new Runnable() {			@Override			public void run() {				line_width = img_smallLine.getWidth();				PalLog.printD(item_width + "," + line_width);				ViewHelper.setTranslationX(img_smallLine,getTransX(0, 0));			}		});		item_width = width / pageCount;	}	public void scroll(int currentIndex, float viewpagerArg) {		ViewHelper.setTranslationX(img_smallLine, getTransX(currentIndex, viewpagerArg));	}	private float getTransX(int currentIndex,float viewpagerArg){		float x=currentIndex * item_width + (viewpagerArg * item_width);		float offset=item_width/2-line_width/2;		return x+offset;	}		public void reSet(){		img_smallLine.post(new Runnable() {			@Override			public void run() {				getTransX(0, 0);			}		});			}	public View getView() {		return mView;	}}